import React from "react";

import axios from "axios";

export const API_URL = 'http://fitnesstrac-kr.herokuapp.com';


export function setToken(token) {
    localStorage.setItem('token', token);
}

export function getToken() {
    return localStorage.getItem('token');
}

export function makeHeader() {
    if (getToken()) {
        return {'Content-Type': 'Application/JSON',
        'Authorization': `Bearer ${getToken()}`}
    } else {
        return {'Content-Type': 'Application/JSON'}
    }
}

export async function signUp(username, password) {
    try {
        const response = await fetch(`${API_URL}/api/users/register`, {
          method: "POST",
          headers: makeHeader(),
          body: JSON.stringify({
            user: {
              username,
              password
              }
            })
          })
          const responseObj = await response.json();
          await console.log('responseObjSignUp', responseObj)

        //   if (responseObj && responseObj.data.token) {
        //   setToken(responseObj.data.token); 
        // }  
        } catch(error){
          console.error(error);
        }
}

export async function signIn(username, password) {
    try {
        const response = await fetch(`${API_URL}/api/users/login`, {
            method: "POST",
            headers: makeHeader(),
            body: JSON.stringify({
                user: {
                    username,
                    password
                }
            })
        })
        const responseObj = await response.json();
        console.log('responseObjSignIn', responseObj) 
        if (responseObj.data) {
            setToken(responseObj.data.token);
        }
    } catch(error){
        console.error(error);
    }
}

export async function fetchUserData() {
    try {
        const response = await fetch(`${API_URL}/users/me`, {
            header: makeHeader(),
        })
        const responseObj = await response.json();
        console.log('responseObjUserData', responseObj)
    } catch(error) {
        console.error(error)
    }
}

export async function fetchActivities() {
    try{
        const response = await fetch(`${API_URL}/api/activities`)
        const data = await response.json();
        console.log('data', data)

    } catch(error) {
      console.error(error)
    }
}

